import Icon from "@iconify/svelte/dist/Icon.svelte"
import {onMount} from 'svelte'

let position = 0
let perPage = 1
let gap = 0
let trueGap = 1
let totalPages = 1
let totalItems = 1
let activeImg = 0
let activeImgSrc = ''
let lightboxOpen = false
let lightboxActive = true

let slideLeft = () => {
  if(position > -((totalItems - 1) * (100 / perPage))) {
    position -= 100 / perPage
    gap = (position < 0) ? gap + trueGap / perPage : 0
  }
}

let slideRight = () => {
  if(position < 0) position += 100 / perPage
  gap = (position < 0) ? gap - trueGap / perPage : 0
}

onMount(async () => {
  window.onresize = () => {
    position = 0
    gap = 0
    calcDimensions()
  }
  totalItems = document.querySelectorAll('.items > figure').length
  calcDimensions()

  document.onkeyup = function (event) {
    let char = (typeof event !== 'undefined') ? event.keyCode : event.which
    if(char == 27) {
      lightboxOpen = false
    }
  }

})

function calcDimensions() {
    switch(true){
      case (innerWidth >= 1200):
        trueGap = 3
        perPage = 4
        lightboxActive = true
        break
      case (innerWidth >= 900):
        perPage = 3
        lightboxActive = true
        break
      case (innerWidth >= 600):
        trueGap = 2
        perPage = 2
        lightboxActive = false
        break
      default:
        trueGap = 1
        perPage = 1
        lightboxActive = false
    }
    totalPages = parseInt(totalItems / perPage)
}

