          &:hover {
            color: var(--color-secondary);
          }
        }
      }
    }
  }
}

@media screen and (min-width: 769px) {
  button#navToggle {
    top: 2.5rem;
    right: 1.5rem;
    transform: scale(1);
  }
  .header {
    .row {
      flex-direction: row;
      &:first-child {
        display: flex;
      }
    }
  }
}

